if (typeof jQuery === 'undefined') {
    var $ = django.jQuery;
}
$('document').ready(function(){
    $('#id_price').keyup(function(){
        var z = $(this).val();
        var a = $('#id_percent').val();
        var kok = (z-(z/100*a))
        if(a == 0){
        $('#id_final_price').val(z);
        }
        else {
        kok = Math.round(kok)
        $('#id_final_price').val(kok);
        }

    });
    $('#id_percent').keyup(function(){
        var z = $(this).val();
        var a = $('#id_price').val();
        var kok = (a-(a/100*z))
        if(z == 0){
        $('#id_final_price').val(a);
        }
        else {
        kok = Math.round(kok)
        $('#id_final_price').val(kok);
        }
    });
});
